mapper.select:
SELECT IMGLIST, SEQ, ID, REGDATE, TRADE, TITLE, PRICE, HEART, HIT FROM ( select * from joonggo order by regdate desc )
( (REGEXP_LIKE(TITLE, #{regexp})) OR (REGEXP_LIKE(CONTENT, #{regexp})) OR (REGEXP_LIKE(TAG, #{regexp})) )
AND TRADE = #{status}
AND CATEGORY IN
#{item}
AND ROWNUM <= 10

SELECT * FROM (SELECT ROWNUM AS NUM, J.* FROM ( SELECT * FROM JOONGGO
( (REGEXP_LIKE(TITLE, #{regexp})) OR (REGEXP_LIKE(CONTENT, #{regexp})) OR (REGEXP_LIKE(TAG, #{regexp})) )
AND TRADE = #{status}
AND CATEGORY IN
#{item}
ORDER BY REGDATE DESC) J) WHERE NUM between (#{page} * 9 + 1) AND ((#{page}+1) * 9 + 1)

mapper.update:
UPDATE JOONGGO
SET HIT = HIT + 1 
WHERE SEQ = #{seq}

UPDATE JOONGGO
SET REGDATE = SYSDATE
WHERE SEQ = #{seq}

UPDATE JOONGGO
SET HEART = HEART + 1
WHERE SEQ = #{seq}

mapper.delete:
DELETE FROM JOONGGO_HEART
WHERE HEARTID = #{heartid}
AND JOONGGOSEQ = #{seq}

mapper.update:
UPDATE JOONGGO
SET HEART = HEART - 1
WHERE SEQ = #{seq}

mapper.select:
SELECT *
FROM JOONGGO_HEART
WHERE HEARTID = #{heartid}
AND JOONGGOSEQ = #{seq}
